#  -*- coding: utf-8 -*-# __name__ = '__main__' import mathdef compute_right_triangle_permeter(radius,height):  permeter = radius + height + math.sqrt(radius * radius + height * height)  return permeterdef compute_right_triangle_area(radius,height):  area = (radius * 0.5) * height  return areadef compute_cone_surface(radius,height):  surface = (math.pi) * radius * (radius + math.sqrt(radius * radius + height * height))  return surfacedef compute_cone_volume(radius,height):  volume = ((math.pi) * radius * radius * height) / 3  return volumedef main():  radius = input("please input the radius ==> ")  height = input("please input the hight ==> ")  radius = float(radius)  height = float(height)  permeter = compute_right_triangle_permeter(radius,height)  area = compute_right_triangle_area(radius,height)  surface = compute_cone_surface(radius,height)  volume = compute_cone_volume(radius,height)  print()  print("----------------------------------------")  print("直角三角形底边长 = %.3f" % radius)  print("直角三角形高 = %.3f" % height)  print("直角三角形面积 = %.3f" % area)  print("直角三角形周长 = %.3f" % permeter)  print("锥形表面积 = %.3f" % surface)  print("锥形体积 = %.3f" % volume)  print("----------------------------------------")if __name__ == '__main__':    main()